(function ($) {

    $.fn.notify = function (options) {

        // This is the easiest way to have default options.
        var settings = $.extend({
            // These are the defaults.
            message: "Hello",
            type: "success"
        }, options);


        var notificationHtml = "<div class='alert alert-" + settings.type + " alert-dismissable'>";
        notificationHtml += '<a href="#" class="close" data-dismiss="alert" aria-label="close">×</a>'
        notificationHtml += " <span class='glyphicon glyphicon-remove-circle'> </span> " + settings.message + "</div>"

        return this.html(notificationHtml);

    };

}(jQuery));
